import React, { useState } from 'react';

interface HeroProps {
  onStartSurvey: () => void;
}

const products = [
  {
    title: 'Immuno Blend',
    badge: 'NEW',
    description: 'Новый комплекс уже в персональных наборах. Капсула, разработанная врачами и технологами Halsa. Добавим в ваш курс, если нужно укрепить иммунитет или восстановиться после болезни.',
    features: [
      'Эффективность состава подтверждена исследованиями',
      'Каждый компонент в формуле усиливает действие других',
      'Премиальные формы компонентов для лучшего усвоения',
    ],
    image: 'https://cdn3d.iconscout.com/3d/premium/thumb/open-capsule-3d-icon-download-in-png-blend-fbx-gltf-file-formats--pill-pharmacy-tablet-medical-pack-healthcare-icons-8639278.png',
  },
  // You can add more products here
];

const bannerMessages = [
  'Бесплатная доставка по России',
  'Скидка 15% на первый заказ',
  'Оплата Долями',
  'Бесплатная консультация врача',
];

const Hero: React.FC<HeroProps> = ({ onStartSurvey }) => {
  const [current, setCurrent] = useState(0);

  // Carousel navigation
  const nextSlide = () => setCurrent((prev) => (prev + 1) % products.length);
  const prevSlide = () => setCurrent((prev) => (prev - 1 + products.length) % products.length);

  return (
    <section className="relative min-h-[90vh] flex items-center bg-gradient-to-br from-brand-green-50 to-white overflow-hidden">
      {/* Carousel */}
      <div className="container mx-auto px-6 max-w-4xl relative z-10 py-12">
        <div className="bg-white/80 rounded-3xl shadow-2xl p-8 flex flex-col md:flex-row items-center gap-8 relative">
          {/* Product Image */}
          <div className="relative flex-shrink-0 w-full md:w-1/2 flex items-center justify-center">
            <div className="absolute -top-6 -left-6 bg-brand-green-100 rounded-3xl w-32 h-32 blur-2xl opacity-40" />
            <img src={products[current].image} alt={products[current].title} className="w-56 md:w-64 rounded-2xl shadow-xl z-10" />
            {products[current].capsule && (
              <img src={products[current].capsule} alt="capsule" className="absolute -bottom-8 left-1/2 -translate-x-1/2 w-16 rotate-12 z-20" />
            )}
            {products[current].badge && (
              <span className="absolute top-2 left-2 bg-brand-green-600 text-white text-xs font-bold px-3 py-1 rounded-full shadow">{products[current].badge}</span>
            )}
          </div>
          {/* Product Info */}
          <div className="flex-1 space-y-4">
            <h2 className="text-2xl md:text-3xl font-bold text-brand-green-900 mb-2">
              {products[current].title} <span className="font-normal text-brand-green-700">— новый комплекс</span>
            </h2>
            <p className="text-brand-green-800 text-base md:text-lg mb-2">{products[current].description}</p>
            <ul className="list-disc pl-5 text-gray-700 text-sm md:text-base mb-4">
              {products[current].features.map((f, i) => (
                <li key={i}>{f}</li>
              ))}
            </ul>
            <button
              onClick={onStartSurvey}
              className="px-8 py-4 bg-brand-green-600 text-white text-lg font-medium rounded-full hover:bg-brand-green-500 transition-all transform hover:scale-105 duration-200 shadow-lg hover:shadow-xl"
            >
              Пройти опрос
            </button>
          </div>
          {/* Carousel navigation */}
          {products.length > 1 && (
            <>
              <button onClick={prevSlide} className="absolute left-2 top-1/2 -translate-y-1/2 bg-white/80 rounded-full p-2 shadow hover:bg-brand-green-100 transition"><span className="sr-only">Prev</span>‹</button>
              <button onClick={nextSlide} className="absolute right-2 top-1/2 -translate-y-1/2 bg-white/80 rounded-full p-2 shadow hover:bg-brand-green-100 transition"><span className="sr-only">Next</span>›</button>
            </>
          )}
        </div>
        {/* Dots */}
        {products.length > 1 && (
          <div className="flex justify-center mt-4 gap-2">
            {products.map((_, idx) => (
              <button
                key={idx}
                onClick={() => setCurrent(idx)}
                className={`w-3 h-3 rounded-full ${idx === current ? 'bg-brand-green-600' : 'bg-gray-300'}`}
                aria-label={`Перейти к продукту ${idx + 1}`}
              />
            ))}
          </div>
        )}
        {/* Running Banner */}
        <div className="mt-8 w-full overflow-hidden rounded-xl bg-brand-green-50 border border-brand-green-100">
          <div className="whitespace-nowrap animate-marquee flex items-center gap-12 py-3 px-4 text-brand-green-800 text-base font-medium">
            {bannerMessages.map((msg, i) => (
              <span key={i} className="flex items-center gap-2">
                {msg}
                {i !== bannerMessages.length - 1 && <span className="mx-2">•</span>}
              </span>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;

// Tailwind CSS for marquee animation (add to your global CSS):
// .animate-marquee {
//   animation: marquee 20s linear infinite;
// }
// @keyframes marquee {
//   0% { transform: translateX(0); }
//   100% { transform: translateX(-50%); }
// }
